import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
// import movieApi from "../../common/apis/movieApi";
// import { APIKEY } from "../../common/apis/movieApiKey";
import axios from "axios";
const MOVIE_URL = "https://www.omdbapi.com/?i=tt3896198&apikey=55944848";
export const fetchAsyncMovies = createAsyncThunk(
  "movies/fetchAsyncMovies",
  async () => {
    try {
      const response = await axios.get(MOVIE_URL);
      return response.data;
    } catch (error) {
      return error.message;
    }
  }
);
console.log();
const initialState = {
  movies: [],
};
const moviesSlice = createSlice({
  name: "movies",
  initialState,
  reducers: {
    addMovies: (state, { payload }) => {
      state.movies = payload;
    },
  },
  extraReducers(builder) {
    builder
      .addCase(fetchAsyncMovies.pending, () => {
        console.log("Pending");
      })
      .addCase(fetchAsyncMovies.fulfilled, (state, action) => {
        const loadedMovies = action.payload.map((movie) => {
          return movie;
        });
        state.movies = state.movies.concat(loadedMovies);
      })
      .addCase(fetchAsyncMovies.rejected, () => {
        console.log("Rejected");
      });
  },
});
export const { addMovies } = moviesSlice.actions;
export const getAllMovies = (state) => state.movies.movies;
export default moviesSlice.reducer;
